mapper.select:
select
  ,
  b.b_name as BName
from

left join tb_billing_type b on b.b_id = g.b_id
where g.data_stat = '0'

  and g.goods_name like "%"#{goodsName}"%"


    and g.spu_code = #{spuCode}


    and g.ecom_code = #{ecomCode}


    and g.goods_type = #{goodsType}


    and g.b_id  = #{BId}


    and g.market_enable = #{marketEnable}


    and g.have_groups = #{haveGroups}


    and g.is_disabled = #{isDisabled}


    and g.is_hot = #{isHot}

select g.goods_id, g.goods_name, g.spu_code, p.sku_code, p.product_id, g.ecom_code, pro.provider_name ecom_name, g.market_enable, p.pic_url, b.b_id, c.cat_name, pc.cat_name as parent_cat_name, p.goods_price, d.view_count, d.buy_count from tb_ecom_goods g inner join tb_ecom_goods_product p on p.goods_id = g.goods_id left join tb_ecom_cat_goods_role r on r.goods_id = g.goods_id left join tb_ecom_goods_billing b on b.goods_id = g.goods_id left join tb_ecom_goods_detail d on d.goods_id = g.goods_id left join tb_ecom_goods_category c on r.cat_id = c.cat_id and c.data_stat = '0' left join tb_provider_inf pro on g.ecom_code = pro.law_code left join tb_ecom_goods_category pc on pc.cat_id = c.parent_id and pc.data_stat = '0' where g.data_stat = '0' and p.data_stat = '0'
and g.ecom_code = #{ecomCode}
and g.spu_code = #{spuCode}
and g.goods_name like '%' || #{goodsName} || '%'
and g.market_enable = #{marketEnable}
and p.sku_code = #{skuCode}
and p.product_id = #{productId}
and b.b_id = #{bId}
group by g.goods_id

select g.goods_id, g.goods_name, g.spu_code, p.sku_code, p.product_id, g.ecom_code, pro.provider_name ecom_name, g.market_enable, p.pic_url, b.b_id, c.cat_name, pc.cat_name as parent_cat_name, p.goods_price, d.view_count, d.buy_count, specv.spec_value from tb_ecom_goods g inner join tb_ecom_goods_product p on p.goods_id = g.goods_id left join tb_ecom_cat_goods_role r on r.goods_id = g.goods_id left join tb_ecom_goods_billing b on b.goods_id = g.goods_id left join tb_ecom_goods_detail d on d.goods_id = g.goods_id left join tb_ecom_goods_category c on r.cat_id = c.cat_id and c.data_stat = '0' left join tb_ecom_goods_category pc on pc.cat_id = c.parent_id and pc.data_stat = '0' left join tb_provider_inf pro on g.ecom_code = pro.law_code left join tb_ecom_goods_spec spec on spec.product_id = p.product_id left join tb_ecom_spec_values specv on specv.spec_value_id = spec.spec_value_id where g.data_stat = '0' and p.data_stat = '0'
and g.ecom_code = #{ecomCode}
and g.spu_code = #{spuCode}
and g.goods_name like '%' || #{goodsName} || '%'
and g.market_enable = #{marketEnable}
and p.sku_code = #{skuCode}
and p.product_id = #{productId}
and b.b_id = #{bId}
group by p.product_id

select
from
where g.data_stat = '0' and g.spu_code = #{spuCode}
