mapper.update:
UPDATE TB_ECOM_MEMBER
PERSON_ID=#{personId,jdbcType=VARCHAR}, USER_ID=#{userId,jdbcType=VARCHAR}, OPEN_ID=#{openId,jdbcType=VARCHAR}, LV_ID=#{lvId,jdbcType=VARCHAR}, POINT=#{point,jdbcType=VARCHAR}, DATA_STAT=#{dataStat,jdbcType=VARCHAR}, REMARKS=#{remarks,jdbcType=VARCHAR}, UPDATE_USER=#{updateUser,jdbcType=VARCHAR}, UPDATE_TIME=sysdate, LOCK_VERSION=LOCK_VERSION+1
WHERE MEMBER_ID=#{membeId}

mapper.select:
SELECT

FROM TB_ECOM_MEMBER M 
WHERE M.DATA_STAT = '0'

	AND	M.PERSON_ID =#{personId}


	AND	M.USER_ID =#{userId}


	AND	M.OPEN_ID =#{openId}
