mapper.select:
select /*item.product_id,item.product_price,item.product_num,item.product_name,*/ po.member_id,po.order_id,po.order_price,po.order_freight_amt,po.pay_status,po.pay_type,po.pay_amt,po.pay_time, pso.s_order_id,pso.ecom_code,pso.sub_order_status, dms.pay_status dms_status,dms.debit_account_code,dms.debit_account_type,dms.debit_price from tb_ecom_platf_order po left join tb_ecom_platf_shop_order pso on po.order_id = pso.order_id left join tb_ecom_dms_related_detail dm on dm.s_order_id = pso.s_order_id left join tb_ecom_pay_order_details dms on dm.pay_details_id = dms.pay_details_id /*left join tb_ecom_order_product_item item on item.s_order_id = pso.s_order_id;*/
po.order_id = #{orderId}
and po.member_id = #{memberId}

select
  ,
    p.mobile_phone_no as mobilePhoneNo,
    p.personal_name as personalName
from

    left join tb_ecom_member m on m.member_id = po.member_id
    left join tb_user_inf u on u.user_id = m.user_id
    left join tb_person_inf p on p.user_id = u.user_id
where po.data_stat = '0'

    and po.order_id = #{orderId}


    and p.personal_name like "%"#{personalName}"%"


    and po.dms_related_key = #{dmsRelatedKey}


    and p.mobile_phone_no = #{mobilePhoneNo}


    and po.pay_status = #{payStatus}


    and po.pay_type = #{payType}

order by po.create_time desc
