mapper.select:
select t.* , b.createtime as createtimestr from t_wx_client_msg_news t , t_wx_client_msg_base b 
where t.base_id = b.id and rownum >= #{param2.start,jdbctype=integer} and rownum <=#{param2.pagesize,jdbctype=integer}
order by b.createtime desc

SELECT t.* from t_wx_client_msg_news t , t_wx_client_msg_base b
WHERE t.base_id = b.id AND rownum <=#{num}
ORDER BY dbms_random.value()
