mapper.select:
select , e.role_name,e.seq from tb_web_user a, tb_web_user_role d, tb_web_role e where a.id = d.user_id and d.role_id = e.id and a.data_stat = '0'
and a.phone_no = #{phoneNo}
and e.seq >= #{seq}
order by e.seq asc

mapper.update:
update tb_web_user a
a.LOGIN_NAME = #{loginName,jdbcType=VARCHAR}, a.PASSWORD = #{password,jdbcType=VARCHAR}, a.USER_NAME = #{userName,jdbcType=VARCHAR}, a.PHONE_NO = #{phoneNo,jdbcType=VARCHAR}, a.ISDEFAULT = #{isdefault,jdbcType=CHAR}, a.DATA_STAT = #{dataStat,jdbcType=CHAR}, a.REMARKS = #{remarks,jdbcType=VARCHAR}, a.UPDATE_USER = #{updateUser,jdbcType=VARCHAR}, a.UPDATE_TIME=#{update_time,jdbcType=VARCHAR}, a.LOCK_VERSION = a.LOCK_VERSION +1
where a.ID = #{id}
