mapper.update:
	update TB_COMPANY_BILLING_TYPE
	set DATA_STAT  = '0',
	UPDATE_TIME=sysdate,
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR}
    WHERE COMPANY_ID = #{cId,jdbcType=VARCHAR} and B_ID = #{bId,jdbcType=VARCHAR}

	UPDATE TB_COMPANY_BILLING_TYPE
	set DATA_STAT  = '1',
	UPDATE_TIME=sysdate,
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR}
    WHERE COMPANY_ID = #{cId,jdbcType=VARCHAR}

	update TB_COMPANY_BILLING_TYPE
	set DATA_STAT  = '1',
	UPDATE_TIME=sysdate,
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR}
    WHERE B_ID = #{bId,jdbcType=VARCHAR}
