mapper.select:
SELECT
FROM TB_COMPANY_INF c WHERE c.DATA_STAT = '0'
AND c.NAME LIKE '%' || #{name,jdbcType=VARCHAR} || '%'
AND c.CONTACTS = #{contacts,jdbcType=VARCHAR}
AND c.TRANS_FLAG = #{flag,jdbcType=VARCHAR}
ORDER BY c.UPDATE_TIME DESC

mapper.update:
	UPDATE TB_COMPANY_INF
    SET DATA_STAT    = '1',
       LOCK_VERSION = LOCK_VERSION + 1,
       UPDATE_TIME  = #{updateTime,jdbcType=BIGINT},
       UPDATE_USER=#{updateUser,jdbcType=VARCHAR}
    WHERE COMPANY_ID = #{cId,jdbcType=VARCHAR}
