mapper.update:
   UPADTE TB_GATEWAY_BILLING_TYPE SET
		DATA_STAT  = '0',
		UPDATE_TIME=sysdate,
		UPDATE_USER= #{updateUser,jdbcType = VARCHAR}
   WHERE GATEWAY_ID = #{gId,jdbcType = VARCHAR} 
        AND B_ID = #{bId,jdbcType = VARCHAR}

mapper.delete:
DELETE FROM TB_GATEWAY_BILLING_TYPE
WHERE B_ID = #{bId,jdbcType = VARCHAR}
      AND GATEWAY_ID = #{gId,jdbcType = VARCHAR}
