mapper.select:
select 

from  m
where m.data_stat='0'

	and m.oper_name like '%' || #{operName} || '%'


	and m.oper_id = #{operId}


	and m.area_flag = #{areaFlag}


	and m.product_type = #{productType}

order by m.update_time desc

select p.product_id, p.oper_name, p.oper_id, p.area_flag, p.product_amt, p.product_price, p.product_type, l.channel_rate from tb_retail_chnl_product_inf p, tb_retail_chnl_item_list l, tb_retail_chnl_inf a where p.data_stat='0' and l.data_stat='0' and a.data_stat='0' and p.product_id=l.product_id and l.channel_id=a.channel_id
and p.oper_id=#{operId}
and p.product_type=#{productType} and p.product_amt=#{productAmt} and a.channel_id=#{channelId}
and p.product_id=#{productId}
