mapper.select:
SELECT m.channel_order_id, m.channel_id, m.outer_tid, m.recharge_phone, m.recharge_type, m.recharge_value, m.item_num, m.pay_amt, m.order_stat, m.notify_stat, m.notify_flag, m.notify_url, m.app_version, m.product_id, m.channel_rate, m.resv1, m.resv2, m.resv3, m.resv4, m.resv5, m.resv6, m.data_stat, m.remarks, m.create_user, m.update_user, m.create_time, m.update_time, m.lock_version, c.channel_name FROM m left join tb_retail_chnl_inf c on m.channel_id = c.channel_id where m.data_stat='0'
and c.channel_name like '%' || #{channelName} || '%'
and m.channel_order_id = #{channelOrderId}
and m.outer_tid = #{outerTid}
and m.recharge_type = #{rechargeType}
and m.order_stat = #{orderStat}
and m.notify_stat = #{notifyStat}
order by m.update_time desc

SELECT 

 		     FROM 	 m
              WHERE m.data_stat='0' AND m.outer_tid=#{outerId} AND m.channel_id=#{channelId}

select o.channel_order_id, p.oper_name, o.recharge_phone, o.item_num, o.recharge_value, o.pay_amt, pr.recharge_state, o.create_time from tb_retail_chnl_order_inf o, tb_retail_chnl_product_inf p, tb_retail_chnl_inf c, tb_tel_provider_order_inf pr where o.product_id = p.product_id and o.channel_id = c.channel_id and o.channel_order_id = pr.channel_order_id and o.data_stat = '0' and p.data_stat = '0' and c.data_stat = '0' and pr.data_stat = '0' and c.resv1 = #{mchntCode}
and o.channel_order_id = #{channelOrderId}
and o.recharge_phone = #{rechargePhone}
and pr.recharge_state = #{rechargeState}
and o.create_time >= #{startDate}
and o.create_time <= #{endDate})
order by o.create_time desc
