mapper.select:
	select 
   		 
    from tb_batch_order_list l
    where l.data_stat='0' 
    
  		and l.order_id = #{orderId} 
  	
  	
  		and l.phone_no=#{phoneNo}
  	
  	
  		and l.account_type=#{accountType}
  	
  	
  		and l.biz_type=#{bizType}
  	
  	
  		and l.order_stat=#{orderStat}
  	
  	
  		and l.order_stat=#{orderStat2}
  	
  	
  		and l.order_stat=#{orderStat3}
  	
  	
  		and l.order_stat=#{orderStat4}

mapper.update:
update tb_batch_order_list
order_id = #{orderId,jdbcType=VARCHAR}, user_name = #{userName,jdbcType=VARCHAR}, phone_no = #{phoneNo,jdbcType=VARCHAR}, user_card_no = #{userCardNo,jdbcType=VARCHAR}, amount = #{amount,jdbcType=VARCHAR}, account_type = #{accountType,jdbcType=VARCHAR}, biz_type = #{bizType,jdbcType=VARCHAR}, tfr_in_id = #{tfrInId,jdbcType=VARCHAR}, tfr_in_bid = #{tfrInBid,jdbcType=VARCHAR}, tfr_out_id = #{tfrOutId,jdbcType=VARCHAR}, tfr_out_bid = #{tfrOutBid,jdbcType=VARCHAR}, order_stat = #{orderStat,jdbcType=VARCHAR}, order_desc = #{orderDesc,jdbcType=VARCHAR}, data_stat = #{dataStat,jdbcType=VARCHAR}, remarks = #{remarks,jdbcType=VARCHAR}, update_user = #{updateUser,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=BIGINT}, lock_version = lock_version + 1
where order_list_id = #{orderListId,jdbcType=VARCHAR}

update.foreach:
update tb_batch_order_list
user_name = #{item.userName,jdbcType=VARCHAR}, phone_no = #{item.phoneNo,jdbcType=VARCHAR}, user_card_no = #{item.userCardNo,jdbcType=VARCHAR}, amount = #{item.amount,jdbcType=DOUBLE}, account_type = #{item.accountType,jdbcType=VARCHAR}, biz_type = #{item.bizType,jdbcType=VARCHAR}, tfr_in_id = #{item.tfrInId,jdbcType=VARCHAR}, tfr_in_bid = #{item.tfrInBid,jdbcType=VARCHAR}, tfr_out_id = #{item.tfrOutId,jdbcType=VARCHAR}, tfr_out_bid = #{item.tfrOutBid,jdbcType=VARCHAR}, order_stat = #{item.orderStat,jdbcType=VARCHAR}, order_desc = #{item.orderDesc,jdbcType=VARCHAR}, data_stat = #{item.dataStat,jdbcType=VARCHAR}, remarks = #{item.remarks,jdbcType=VARCHAR}, update_user = #{item.updateUser,jdbcType=VARCHAR}, update_time = #{item.updateTime,jdbcType=BIGINT}, lock_version = lock_version + 1
order_list_id = #{item.orderListId,jdbcType=VARCHAR}
