mapper.select:
		select
			,
			c.name as companyName,
			(select count(l.order_list_id)
				from tb_batch_order_list l
				where l.data_stat='0' and o.order_id = l.order_id
			) as orderCount,
			(select sum(l.amount)
				from tb_batch_order_list l
				where l.data_stat = '0' and l.order_id = o.order_id
			) as sumAmount
		from tb_batch_order o left join tb_company_inf c 
			on o.company_id = c.company_id
		where o.data_stat = '0' 
			and c.data_stat = '0'
			and o.order_type = #{orderType}
		
			and o.order_id = #{orderId}
		
		
			and o.order_name like '%'|| #{orderName} ||'%'
		
		
			and o.order_stat = #{orderStat}
		
		
			and c.company_Name like '%'|| #{companyName} ||'%'
		
		
			and o.create_time >= #{startTime}
		
		
			and o.create_time <= #{endTime}
		
		order by o.update_time desc

select 
	
from tb_batch_order o
where o.order_id = #{orderId}

mapper.update:
update tb_speaccount_batch_order
order_name = #{orderName,jdbcType=VARCHAR}, order_type = #{orderType,jdbcType=VARCHAR}, order_stat = #{orderStat,jdbcType=VARCHAR}, data_stat = #{dataStat,jdbcType=VARCHAR}, remarks = #{remarks,jdbcType=VARCHAR}, update_user = #{updateUser,jdbcType=VARCHAR}, company_id = #{companyId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=BIGINT}, lock_version = lock_version+1
where order_id = #{orderId,jdbcType=VARCHAR} and lock_version = ${lockVersion}
